mapper.select:
SELECT COUNT(*) FROM "BOARD"
WHERE BOARD_DEL_FL = 'N'
AND BOARD_CODE = #{boardCode}

SELECT BOARD_NO,BOARD_TITLE,MEMBER_NICKNAME,WRITE_DATE,

(SELECT COUNT(*) FROM "BOARD_LIKE" L 
	WHERE L.BOARD_NO = B.BOARD_NO) LIKE_COUNT

FROM BOARD B
JOIN "MEMBER" USING("MEMBER_NO")
WHERE BOARD_CODE = #{boardCode}
 	AND CATEGORY_CODE = #{categoryCode}
 	AND CATEGORY_CODE_TWO = #{categoryCodeTwo}

AND BOARD_DEL_FL = 'N'
ORDER BY BOARD_NO DESC

SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, BOARD_COUNT, TO_CHAR(WRITE_DATE, 'YYYY"년" MM"월" DD"일" HH24:MI:SS')WRITE_DATE, MEMBER_NICKNAME, PROFILE_IMG, MEMBER_NO, PROFILE_IMG,
(SELECT COUNT(*) FROM "BOARD_LIKE" L WHERE L.BOARD_NO = B.BOARD_NO) LIKE_COUNT
FROM "BOARD" B JOIN "MEMBER" USING(MEMBER_NO) WHERE BOARD_DEL_FL = 'N' AND BOARD_CODE = #{boardCode} AND BOARD_NO = #{boardNo}

SELECT * FROM BOARD_IMG
WHERE BOARD_NO = #{boardNo}
ORDER BY IMG_ORDER

SELECT COUNT(*) FROM BOARD_LIKE
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE 
FROM "BOARD_LIKE"
WHERE MEMBER_NO  = #{memberNo}
AND BOARD_NO =  #{boardNo}

mapper.update:
UPDATE "BOARD" SET
BOARD_COUNT = BOARD_COUNT +1
WHERE BOARD_NO = #{boardNo}
