mapper.select:
SELECT COUNT(*) FROM BOARD JOIN "MEMBER" USING("MEMBER_NO") WHERE BOARD_DEL_FL = 'N' AND BOARD_CODE = '2'
AND CATEGORY_CODE = #{c1}
AND CATEGORY_CODE_TWO = #{c2}
ORDER BY BOARD_NO DESC

SELECT BOARD_NO,BOARD_TITLE, BOARD_CONTENT, MEMBER_NICKNAME, WRITE_DATE FROM BOARD JOIN "MEMBER" USING("MEMBER_NO") WHERE BOARD_DEL_FL = 'N' AND BOARD_CODE = '2'
AND CATEGORY_CODE = #{c1}
AND CATEGORY_CODE_TWO = #{c2}
ORDER BY BOARD_NO DESC

SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, BOARD_COUNT, WRITE_DATE, PROFILE_IMG, MEMBER_NO, PROFILE_IMG,
(SELECT COUNT(*) FROM "BOARD_LIKE" L WHERE L.BOARD_NO = B.BOARD_NO) LIKE_COUNT
FROM "BOARD" B JOIN "MEMBER" USING(MEMBER_NO) WHERE BOARD_DEL_FL = 'N' AND BOARD_CODE = '2' AND BOARD_NO = #{boardNo}
