mapper.select:
SELECT BOARD_VIDEO_NO, VIDEO_TITLE, VIDEO_CONTENT, VIDEO_READCOUNT, TO_CHAR(VIDEO_WRITE_DATE, 'YYYY"년" MM"월" DD"일" HH24:MI:SS') VIDEO_WRITE_DATE, TO_CHAR(VIDEO_UPDATE_DATE, 'YYYY"년" MM"월" DD"일" HH24:MI:SS') VIDEO_UPDATE_DATE, MEMBER_NICKNAME, PROFILE_IMG, MEMBER_NO,
(SELECT COUNT(*) FROM "VIDEO_LIKE" L WHERE L.BOARD_VIDEO_NO = V.BOARD_VIDEO_NO) LIKE_COUNT
FROM "VIDEO_BOARD" V JOIN "MEMBER" USING(MEMBER_NO) WHERE VIDEO_DEL_FL = 'N' AND BOARD_VIDEO_NO = #{boardVideoNo}

SELECT COUNT(*) FROM "VIDEO_LIKE"
WHERE BOARD_VIDEO_NO = #{boardVideoNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE "VIDEO_BOARD" SET
VIDEO_READCOUNT = VIDEO_READCOUNT + 1
WHERE BOARD_VIDEO_NO = #{boardVideoNo}

mapper.delete:
DELETE FROM VIDEO_LIKE
WHERE BOARD_VIDEO_NO = #{boardVideoNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE VIDEO_BOARD SET
VIDEO_DEL_FL = 'Y'
WHERE BOARD_VIDEO_NO = #{boardVideoNo}
AND MEMBER_NO = #{memberNo}

UPDATE VIDEO SET
VIDEO_PATH = #{videoPath},
VIDEO_RENAME = #{videoRename},
VIDEO_ORIGINALNAME = #{videoOriginalName}
WHERE BOARD_VIDEO_NO = #{boardVideoNo}
AND VIDEO_ORDER = #{videoOrder}
